
public class if_else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name="halima";
//       only one block excecute at a time
       if(name=="alfiya") {
    	   System.out.println("your name is alfiya");
       }
       
//       can write multiple else if condition
       else if(name=="sumaiya") {
    	   System.out.println("you are sumaiya");
       }
       
       else {
    	   System.out.println("you are halima");
       }
	}

}
